    private void botonHoraActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonHoraActionPerformed
        Horario mod = new Horario();
        VistaHoras vista = new VistaHoras();
        controladorHora con = new controladorHora(vista, mod);
        con.start();
        vista.setVisible(true);
        txtHorario.setText(con.getHorario_actual());
    }//GEN-LAST:event_botonHoraActionPerformed


